defaultText:
{{  
  GetThreadMessages.code === 0 
  ? 'No items found'  // Handle the error case when code is 0
  : GetThreadMessages.data?.data 
    ? GetThreadMessages.data.data.reverse().map(item => {
        const role = item.role ? item.role : 'none';  // Set 'role' to 'none' if no value is returned

        return role === 'user' 
          ? `**${role.toUpperCase()}:** 

\`\`\`diff
+ ${item.content[0]?.text?.value || 'No content available'}
\`\`\`
`
          : `**${role.toUpperCase()}:** 

${item.content[0]?.text?.value || 'No content available'}`
      }).join('\n\n---\n\n')
    : 'No messages available'  // Handle the case where data is undefined
}}
